
CREATE TABLE MISSIONS (ID NUMBER GENERATED ALWAYS as IDENTITY(START with 1 INCREMENT by 1), TITLE_AR VARCHAR2(200), TITLE_FR VARCHAR2(200), DESCRIPTION_AR VARCHAR2(400), DESCRIPTION_FR VARCHAR2(4000)
CREATE TABLE ACTIONS (ID NUMBER GENERATED ALWAYS as IDENTITY(START with 1 INCREMENT by 1), TITLE_AR VARCHAR2(200), TITLE_FR VARCHAR2(200), START_DATE DATE, END_DATE DATE, BUDGET_FR ?, BUDGET_FR ?);
CREATE TABLE ADMINISTRATIONS (ID NUMBER GENERATED ALWAYS as IDENTITY(START with 1 INCREMENT by 1), TITLE_AR VARCHAR2(200), TITLE_FR VARCHAR2(200), DESCRIPTION_AR VARCHAR2(400), DESCRIPTION_FR VARCHAR2(4000), NIVEAU ?, PARENT_ID NUMBER);
CREATE TABLE RECOMMANDATIONS (MISSIONS_ADMINISTRATIONS_ID NUMBER, CODE NUMBER, TITLE_AR VARCHAR2(200), TITLE_FR VARCHAR2(200), START_DATE DATE, END_DATE DATE, ESCOMPTE_AR ?, ESCOMPTE_FR ?, OBTENU_AR ?, OBTENU_FR ?, OBSERVATIONS_AR VARCHAR2(400), OBSERVATIONS_FR VARCHAR2(4000));
CREATE TABLE MISSIONS_ADMINISTRATIONS (MISSION_ID NUMBER, ADMINISTRATION_ID NUMBER);
CREATE TABLE USERS (NOM VARCHAR2(200), USERNAME VARCHAR2(200), EMAIL VARCHAR2(200), PASSWORD VARCHAR2(200));


INSERT INTO RECOMMANDATION (TITLE_FR, DESCRIPTION_FR) VALUES ('La SONACOS (Décembre 2019)','Il s''agit de la refonte des structures admnisitratives organisationnelles de telle sorte qu''elle permettra aux différents départements ministériels administratifs, au niveau central et local, d''assurer la mise en oeuvre efficace et performante des politiques publiques et de fournir les services publics en respectant les normes de qualité aux usagers.')
